import React, { useContext, useState, useRef, useEffect } from 'react'
import AppContext from '../appContext'
import {
    getEthRates,
    fetchConversionRate
} from '../utils/conversionRate';

const Create = () => {
    const { account, hasWalletAddressAfterChange, greenCollectibleContract, web3 } = useContext(AppContext);
    const inputRef = useRef()
    const [donation, setDonation] = useState(0.001)
    const [selectedFile, setSelectedFile] = useState({ src: '', alt: '' });
    const [ethRatesForDonation, setEthRatesForDonation] = useState({ eth: '', usd: '' })

    const clickInputField = () => {
        inputRef.current.click()
    }

    useEffect(() => {
        const runEffect = async () => {
            try {
                await fetchConversionRate();
                setEthRatesForDonation(getEthRates(parseFloat(donation)));
            } catch (err) {
                console.log(err)
            }
        };
        runEffect();
    }, [donation, web3.utils]);

    //https://dev.to/yosraskhiri/make-an-image-preview-in-react-js-301f
    const handlePickFile = (e) => {
        if (e.target.files[0]) {
            setSelectedFile({
                src: URL.createObjectURL(e.target.files[0]),
                alt: e.target.files[0].name
            });
        }
    }

    const removeFile = () => {
        setSelectedFile({
            src: '',
            alt: ''
        });
    }

    const handleDonationInput = (e) => {
        //1231. is valid at this point - we would cover this case on submit!
        const input = e.target.value
        const regExFloatNotStrict = /^([0-9]*[.])?([0-9]?)+$/;
        if (input === '' || regExFloatNotStrict.test(input)) {
            setDonation(input)
        }
    }

    return (
        <div className='flex w-full flex-col h-screen space-y-8 mt-4 sm:mt-12 items-center bg-grey-lighter'>
            <h1 className="sm:text-4xl text-2xl font-bold">Create a <span className='text-green-700'>green</span> collectible</h1>
            <div className='relative border-2 rounded-lg p-8 space-y-4'>
                <div className='absolute right-0 top-0 p-2 border-l-2 border-b-2'>
                    <span className='font-bold text-gray-400'>ERC-721</span>
                </div>
                <div>
                    <label className='font-bold'>Upload file</label>
                    <div className="relative w-64 sm:w-96 my-2 flex flex-col items-center px-4 py-6 bg-white rounded-lg shadow-md tracking-wide border-2 border-dashed border-black">
                        {selectedFile.src === '' ?
                            <div className='flex flex-col space-y-2 items-center'>
                                <span className="text-xs leading-normal">PNG, JPEG, GIF or WEBP. Max 20MB.</span>
                                <svg className="w-8 h-8" fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                                    <path d="M16.88 9.1A4 4 0 0 1 16 17H5a5 5 0 0 1-1-9.9V7a3 3 0 0 1 4.52-2.59A4.98 4.98 0 0 1 17 8c0 .38-.04.74-.12 1.1zM11 11h3l-4-4-4 4h3v3h2v-3z" />
                                </svg>
                                <button className='bg-white border-2 hover:shadow-md hover:border-green-700 hover:text-green-700 px-14 py-2 font-bold rounded-lg' onClick={clickInputField}>
                                    <span className="mt-2 text-base leading-normal">Choose File</span>
                                </button>
                                <input ref={inputRef} type='file' className="hidden" accept="image/png,image/jpeg,image/gif,image/webp" onChange={handlePickFile} />
                            </div> : <div><img className='max-w-xs' src={selectedFile.src} alt={selectedFile.alt} />
                                <button className="absolute right-0 top-0 m-2 bg-grey-light hover:bg-grey text-grey-darkest font-bold rounded inline-flex items-center"
                                    onClick={removeFile}>
                                    <img src='remove.png' alt='remove picked file' width='35' height='35' />
                                </button></div>}
                    </div>
                </div>
                <div className='w-72 sm:w-96'>
                    <label className='font-bold'>Donate to a non-profit organization:</label>
                    <div className='flex my-2 border-2 shadow-md rounded justify-between'>
                        <div>
                            <input className='font-bold focus:outline-none whitespace-nowrap text-base p-4 sm:text-2xl sm:px-4 border-r-2 sm:w-64'
                                placeholder='e.g. 1.2' value={donation} inputMode='decimal' onChange={handleDonationInput} />
                        </div>
                        <div className='flex flex-col w-full justify-center sm:flex-row items-center sm:space-x-2'>
                            <img
                                src='data:image/png;base64,'
                                alt='eth img'
                                width='30'
                                height='30' />
                            <span>ETH</span>
                        </div>
                    </div>
                    <div className='flex flex-row justify-center items-center'>
                        <span>The amount equals to {Math.floor(ethRatesForDonation.usd)} new</span>
                        <img className='mx-2' src='tree.png' alt='trees' width='35' height='35' />
                        <span>planted!</span>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Create
